// global modules
import ready from "/js/modules/ready.min.js";
import loadData from "/js/modules/loadData.min.js";
import getUrlParams from "/js/modules/getUrlParams.min.js";

// local modules
import signIn from "./modules/signIn.min.js";
import getData from "./modules/getData.min.js";
import startGame from "./modules/startGame.min.js";

ready(async () => {
  let userData;
  const signInForm = document.getElementById("sign-in-form");
  const skiptToResultsBtn = document.getElementById("skip-to-results");

  skiptToResultsBtn.addEventListener("click", () => {
    window.skipToResults = true;
    skiptToResultsBtn.innerHTML = "Skipping...";
    skiptToResultsBtn.disabled = true;
  });

  signInForm.addEventListener("submit", signIn);
  userData = await loadData("fediverseFollowsData");

  if (userData) {
    getUrlParams(true);
    startGame(userData);
  } else {
    getData();
  }
});

